"use strict";

var db = require('./db.js');
var schemes = require('./schemes.js');

module.exports = {

    /**
     * Add The model in models so instanced can be called like this: models.user.getInstance(...);
     * @param {Object} model
     * @param {Object} scheme
     */
    registerModel: function (type, model, scheme) {

        //var newvar = new model();
        //console.log(newvar);

        if (this[type] === undefined) {
            this[type] = model;
        }
    }
};