module.exports = {
  /**
   * Saves a given schema for given model type, so it can be used later for validation
   * Schemas are saved under the given modelType key. schema.user = users_schema;
   * @param {string} modelType
   * @param {object} schema
   */
  registerModel: function (modelType, schema) {
    if (this.modelType === undefined) {
      this[modelType] = schema;
    }
  }
};