
/**
 * @param  {Array} urls Array of url objects
 * @param  {Object} app Application object
 * @return {function}
 */
var urls = module.exports = function(urls, app) {
	var _urls = urls;
	var _length = _urls.length;


	for (var i = 0; i < _length; i++ ) {
	    var url = _urls[ i ];
	    if(url.method == "post") {
	    	app.set(url.pattern, url.view);
	    } else {
	    	app.all(url.pattern, url.view);
	    }
	};

	/**
	 * Create template helper, so user can user "url name" and get the url
	 */
	app.locals.url = function(name) {
	    for ( var i = 0; i < _length; i++ ) {
	        var url = _urls[i];
	        if ( url.name === name ) {
	            return url.pattern;
	        }
	    };
	};

}
